import React from "react";
import emailjs from "emailjs-com";

function Form() {
  function sendEmail(e) {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_o6o020o",
        "template_mfk7zkp",
        e.target,
        "user_KjjfI0v4i1et2qcGTflNQ"
      )
      .then((res) => {
        console.log(res);
      })
      .catch((err) => console.log(err));
  }
  return (
    <div className="min-h-screen flex items-center justify-center">
      {/* Heading */}
      <div className=" p-10 rounded  w-full  max-w-lg">
        <h2 className="text-4xl md:text-6xl font-thin mb-2">Interested?</h2>
        <p className="mb-6 text-gray-700 font-bold text-xs">
          Book a viewing today and a member of our team will be in touch.
        </p>
        {/* Form */}
        <form className="space-y-4" onSubmit={sendEmail}>
          {/* Name */}
          <div>
            <label htmlFor="name" className="block mb-2 text-gray-800">
              Name
            </label>
            <input
              type="text"
              className="border-2 border-gray-200 w-full p-2 rounded outline-none focus:border-blue-700"
              id="name"
              name="user_name"
            />
          </div>
          {/* Email */}
          <div>
            <label for="name" className="block mb-2 text-gray-800">
              Email
            </label>
            <input
              type="email"
              className="border-2 border-gray-200 w-full p-2 rounded outline-none focus:border-blue-700"
              id="email"
              name="user_email"
            />
          </div>
          {/* Checkbox */}
          <div className="flex items-center">
            <input type="checkbox" id="agree" />
            <label for="agree" className="ml-2 text-gray-700 text-sm">
              I agree to the terms and conditions.
            </label>
          </div>
          {/* Button */}
          <button className="block bg-green-400 hover:bg-blue-500 transition text-white duration-300 w-full p-2 rounded">
            Sign up
          </button>
        </form>
      </div>
    </div>
  );
}

export default Form;
