import React from "react";
import golf from "./golf1.jpg";

function Hero() {
  return (
    <div className="relative top-0">
      <div className="w-full h-full flex flex-col absolute top-0 left-0 justify-center items-left md:items-center">
        {/*Heading*/}
        <div className="text-4xl font-bold text-blue-700 md:text-6xl sm:text-4xl">
          e-Golf
        </div>
        {/*Text*/}
        <div className="text-lg sm:text-md text-blue-700">Electric motor</div>
      </div>
      {/*Image*/}
      <img src={golf} alt="The new e-Golf" />
    </div>
  );
}

export default Hero;
