import React from "react";
import golf from "./golf1.jpg";

function Hero() {
  return (
    <div class="flex items-center justify-center pt-10">
      <div class="flex items-center flex-col-2 justify-between text-white">
        <div class="flex flex-col ml-20 mx-20 gap-4">
          <span class="text-2xl font-bold text-blue-700 md:text-6xl">
            The new e-Golf
          </span>
          <a href="#">
            <button className="text-xs md:w-60 tracking-widest px-2 py-2 hover:bg-blue-800 text-center w-30 bg-blue-700 text-white rounded">
              {/* text-xs tracking-widest py-4 px-4 bg-blue-700 hover:bg-blue-800 text-white rounded transition duration-300 
            text-gray-400 text-xs uppercase hover:text-blue-700 py-4 px-4 hover:bg-blue-800 rounded

            */}
              Take the tour
            </button>
          </a>

          {/**
           * <p className="text-gray-400 text-sm">Electric never felt so good.</p>
           */}
        </div>
        <img
          src={golf}
          alt="The new e-Golf"
          className="aspect-w-16 relative  aspect-h-2 object-contain md:object-scale-down mx-auto"
        ></img>
      </div>
    </div>
  );
}

export default Hero;
