import React from "react";
import golf from "./golf1.jpg";

function Hero() {
  return (
    <div class="flex items-center justify-center pt-10 overflow-hidden">
      <div class="flex items-center justify-between text-white gap-8">
        <div class="flex flex-col pl-12 w-40">
          <span class="text-gray-900 tracking-widest font-bold text-xs uppercase">
            The new
          </span>

          <span class="text-4xl text-blue-600 hover:text-blue-400 pb-5">
            e-Golf
          </span>
          <a href="#">
            <button className="text-xs lg:w-100 md:w-36 tracking-widest px-2 py-2 uppercase hover:bg-blue-800 z-60 text-center w-24 bg-blue-700 text-white rounded">
              {/* text-xs tracking-widest py-4 px-4 bg-blue-700 hover:bg-blue-800 text-white rounded transition duration-300 
            text-gray-400 text-xs uppercase hover:text-blue-700 py-4 px-4 hover:bg-blue-800 rounded

            */}
              Take the tour
            </button>
          </a>

          {/**
           * <p className="text-gray-400 text-sm">Electric never felt so good.</p>
           */}
        </div>
        <img
          src={golf}
          alt="The new e-Golf"
          className="aspect-w-16 pl-12 relative aspect-h-2 object-cover rounded-lg md:object-scale-down mx-auto "
        ></img>
      </div>
    </div>
  );
}

export default Hero;
